window.addEventListener("DOMContentLoaded",(()=>{let e=document.querySelector(".menu__icon"),t=document.querySelector(".nav");e&&e.addEventListener("click",(()=>{document.body.classList.toggle("lock"),e.classList.toggle("_active-icon"),t.classList.toggle("_active")}));const o=document.querySelectorAll('a[href*="#"]');for(let c of o)c.addEventListener("click",(function(o){o.preventDefault(),e.classList.contains("_active-icon")&&(document.body.classList.remove("lock"),e.classList.remove("_active-icon"),t.classList.remove("_active"));const s=c.getAttribute("href").substr(1);document.getElementById(s).scrollIntoView({behavior:"smooth",block:"start"})}))}));